import React from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const TaskList = ({ tasks, onReorder, onUpdate, onDelete }) => {
  const handleOnDragEnd = (result) => {
    if (!result.destination) return; // Si no hay destino, no hacemos nada

    const reorderedTasks = Array.from(tasks); // Hacemos una copia del array de tareas
    const [removed] = reorderedTasks.splice(result.source.index, 1); // Quitamos la tarea arrastrada
    reorderedTasks.splice(result.destination.index, 0, removed); // Insertamos la tarea en el nuevo lugar

    onReorder(reorderedTasks); // Actualizamos el estado con el nuevo orden
  };

  return (
    <DragDropContext onDragEnd={handleOnDragEnd}>
      <Droppable droppableId="taskList">
        {(provided) => (
          <ul ref={provided.innerRef} {...provided.droppableProps}>
            {tasks.map((task, index) => (
              <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                {(provided) => (
                  <li
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={{
                      padding: '10px',
                      border: '1px solid #ccc',
                      marginBottom: '10px',
                      background: 'white',
                      borderRadius: '4px',
                      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                      ...provided.draggableProps.style, // Para manejar el arrastre visualmente
                    }}
                  >
                    <span>{task.title}</span>
                    <button onClick={() => onUpdate(task)}>Editar</button>
                    <button onClick={() => onDelete(task.id)}>Eliminar</button>
                  </li>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </ul>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default TaskList;
