import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import './App.css';
import { useTheme } from './ThemeContext'; // Importa el contexto de tema

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [editingTask, setEditingTask] = useState(null); // Estado para la tarea en edición
  const { theme, toggleTheme } = useTheme(); // Accede al tema y la función para cambiarlo

  // Obtener todas las tareas
  const fetchTasks = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8081/tasks');
      if (!response.ok) {
        throw new Error('Error al obtener las tareas');
      }
      const data = await response.json();
      setTasks(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Crear una nueva tarea
  const createTask = async (task) => {
    try {
      const response = await fetch('http://localhost:8081/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(task),
      });
      if (!response.ok) {
        throw new Error('Error al crear la tarea');
      }
      const newTask = await response.json();
      setTasks([...tasks, newTask]);
    } catch (error) {
      setError(error.message);
    }
  };

  // Actualizar una tarea existente
  const updateTask = async (id, updatedTask) => {
    try {
      const response = await fetch(`http://localhost:8081/tasks/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTask),
      });
      if (!response.ok) {
        throw new Error('Error al actualizar la tarea');
      }
      const updatedTaskData = await response.json();
      setTasks(tasks.map(task => (task.id === id ? updatedTaskData : task)));
    } catch (error) {
      setError(error.message);
    }
  };

  // Eliminar una tarea
  const deleteTask = async (id) => {
    try {
      const response = await fetch(`http://localhost:8081/tasks/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Error al eliminar la tarea');
      }
      setTasks(tasks.filter(task => task.id !== id));
    } catch (error) {
      setError(error.message);
    }
  };

  // Reordenar tareas
  const handleReorder = (reorderedTasks) => {
    setTasks(reorderedTasks);
  };

  // Activar la edición de una tarea
  const startEditing = (task) => {
    setEditingTask(task); // Guardar la tarea que está siendo editada
  };

  // Cancelar la edición
  const cancelEditing = () => {
    setEditingTask(null);
  };

  // Cargar las tareas al iniciar la aplicación
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className={`App ${theme === 'dark' ? 'bg-dark text-white' : 'bg-light text-dark'}`}>
      <h1>Lista de Tareas</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {loading ? (
        <p>Cargando...</p>
      ) : (
        <>
          <button onClick={toggleTheme} className="btn btn-secondary">
            Cambiar a {theme === 'dark' ? 'Claro' : 'Oscuro'}
          </button>
          <TaskForm onCreate={createTask} task={editingTask} onCancel={cancelEditing} onUpdate={updateTask} />
          <TaskList tasks={tasks} onUpdate={startEditing} onDelete={deleteTask} onReorder={handleReorder} />
        </>
      )}
    </div>
  );
}

export default App;
