import React, { useState, useEffect } from 'react';

function TaskForm({ onCreate, onUpdate, task }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  // Si estamos editando una tarea, llenar los campos del formulario
  useEffect(() => {
    if (task) {
      setTitle(task.title);
      setDescription(task.description);
    }
  }, [task]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTask = { title, description };
    if (task) {
      // Si estamos editando, actualizar la tarea
      onUpdate(task.id, newTask);
    } else {
      // Si estamos creando una nueva tarea
      onCreate(newTask);
    }
    setTitle('');
    setDescription('');
  };

  return (
    <form onSubmit={handleSubmit} className="mb-3">
      <input
        type="text"
        placeholder="Título"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
        className="form-control mb-2"
      />
      <input
        type="text"
        placeholder="Descripción"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="form-control mb-2"
      />
      <button type="submit" className="btn btn-primary">{task ? 'Actualizar Tarea' : 'Agregar Tarea'}</button>
      {task && <button type="button" onClick={() => { setTitle(''); setDescription(''); }}>Cancelar</button>}
    </form>
  );
}

export default TaskForm;
